when.foreach:
UPDATE AIRQUALITY_INFO
SET Date = #{item.dataTime}
,FineDustConcentration = #{item.pm10Value}
,UltrafineDustConcentration = #{item.pm25Value}
,OzoneConcentration = #{item.o3Value}
WHERE CityId = #{item.cityId}

